<script>
import axios from 'axios'
import ContentContainerComponent from '@/components/ContentContainerComponent'
import $RefParser from 'json-schema-ref-parser'
import CardComponent from '../CardComponent'

// eslint-disable-next-line no-unused-vars
import { signTx } from 'signcosmostx/signStuff'

export default {
  name: 'NewCardPage',
  components: {CardComponent, ContentContainerComponent},
  data () {
    return {
      activeStep: 0,
      fileURL: 'data:image/jpeg;base64,',
      model: {
        name: 'Name',
        description: '',
        ability: '',
        notes: '',
        article: 'the',
        surname: 'Surname',
        type: 'No Type',
        tags: [],
        cost: {
          lumber: 0,
          food: 0,
          iron: 0,
          mana: 0,
          energy: 0,
          generic: 0
        },
        ticks: 0,
        defense: 0,
        attack: 0
      },
      rules: {},
      cardID: 0
    }
  },
  mounted () {
    if (localStorage.cardDraft) {
      this.model = JSON.parse(localStorage.cardDraft)
    }
    $RefParser.dereference('https://gracious-hopper-f1dfd1.netlify.com/cardSchema.json', (err, api) => {
      if (err) {
        console.log(err)
      } else {
        this.rules = api
        console.log(api)
      }
    })
  },
  computed: {
    remainingCosts () {
      return 32 - this.model.cost.lumber -
              this.model.cost.food -
              this.model.cost.iron -
              this.model.cost.mana -
              this.model.cost.energy -
              this.model.cost.generic
    }
  },
  methods: {
    register () {
      axios.post(
        this.apiURL + '/cardservice/save_card_content',
        {
          'base_req': {
            'from': 'globally fetched users key',
            'chain_id': this.chainID,
            'sequence': '2',
            'account_number': '0',
            'gas': 'auto',
            'gas_adjustment': '1.5'
          },
          'owner': 'globally fetched users key',
          'content': this.generatedContent,
          'cardid': this.cardID
        }).then(response => (this.cards = response.data))
    },
    getNumbers (start, stop, min) {
      /* if (min >= stop) {
        return new Array(min + 1 - start).fill(start).map((n, i) => n + i)
      } else {
        return new Array(stop + 1 - start).fill(start).map((n, i) => n + i)
      } */
      return [1, 2, 3, 4, 5]
    },
    buyCardScheme () {
      axios.get('http://78.46.200.30/auth/accounts/' + localStorage.cosmosAddress)
        .then(userdata => {
          axios.post('http://78.46.200.30/cardservice/buy_card_scheme', {
            'base_req': {
              'from': localStorage.cosmosAddress,
              'chain_id': 'testCardchain',
              'gas': 'auto',
              'gas_adjustment': '1.5'
            },
            'amount': '800credits',
            'buyer': localStorage.cosmosAddress
          }).then(response => {
            let signed = signTx(response.data, localStorage.cosmosMnemonic, 'testCardchain', userdata.data.value.account_number, userdata.data.value.sequence)

            console.log(signed)

            axios.post('http://78.46.200.30/txs', {
              'tx': signed.value,
              'mode': 'block'
            }).then(response => {
              console.log(response)
            })
          })
        })
    },
    generateCostArray () {
      let finalArr = []

      for (let i = 0; i < this.model.cost.energy; i++) {
        finalArr.push('ENERGY')
      }
      for (let i = 0; i < this.model.cost.food; i++) {
        finalArr.push('FOOD')
      }
      for (let i = 0; i < this.model.cost.generic; i++) {
        finalArr.push('GENERIC')
      }
      for (let i = 0; i < this.model.cost.iron; i++) {
        finalArr.push('IRON')
      }
      for (let i = 0; i < this.model.cost.lumber; i++) {
        finalArr.push('LUMBER')
      }
      for (let i = 0; i < this.model.cost.mana; i++) {
        finalArr.push('MANA')
      }

      return finalArr
    },
    saveSubmit () {
      let costArray = this.generateCostArray()
      // eslint-disable-next-line no-unused-vars
      let newCard = {
        [this.model.type]: {
          'Name': this.model.name,
          'Tag': this.model.tags,
          'Taxt': this.model.description,
          'Cost': costArray,
          'CastSpeed': this.model.ticks,
          'Effects': {}
        }
      }

      let request = {
        'base_req': {
          'from': localStorage.cosmosAddress,
          'chain_id': 'testCardchain',
          'gas': 'auto',
          'gas_adjustment': '1.5'
        },
        'owner': localStorage.cosmosAddress,
        'content': JSON.stringify(newCard),
        'cardid': '1'
      }

      axios.get('http://78.46.200.30/auth/accounts/' + localStorage.cosmosAddress)
        .then(userdata => {
          console.log(userdata)
          axios.put(
            'http://78.46.200.30/cardservice/save_card_content',
            request).then(response => {
            let signed = signTx(response.data, localStorage.cosmosMnemonic, 'testCardchain', userdata.data.value.account_number, userdata.data.value.sequence)

            console.log(signed)

            axios.post('http://78.46.200.30/txs', {
              'tx': signed.value,
              'mode': 'block'
            }).then(response => {
              console.log(response)
            })
          })
        })
    }
  },
  saveDraft () {
    localStorage.cardDraft = JSON.stringify(this.model)
  },
  onFileChange (e) {
    const file = e.target.files[0]
    this.url = URL.createObjectURL(file)
  }
}
</script>

